import React, { useState } from 'react';
import './Gallery.css';
import axios from 'axios';

function Gallery() {

    const [photo,setPhoto] = useState('');
    const [clientId,setClientid] = useState('iU1nsbeOMcgen6Xb1x-T4zIGER6YnvJ3zPUc5ovOXew');

    const [result,setResult] = useState([]);
    
    function handleChange(event) {
        setPhoto(event.target.value);
    }

    function handleSubmit(event) {
        console.log(photo);
        
        const url = 'https://api.unsplash.com/search/photos?page=1&query=';
        
        axios.get(url).then(response => {
            console.log(response);
            setResult(response.data.result)
        });
    };

  return (
    <div className='gallery'>
        <h1>Gallery</h1>
        <p>Here you can search whole lot of images from world wide stack.</p>
        <input onChange={handleChange} className='input-box' type='text' name='photos' placeholder='Search for Images here...' />
        <br/>
        <br/>
        <button onClick={handleSubmit} type='submit'>Search</button>
        
        {result.map(photo => (
            <img src='photo.urls.small'/>
        ))};
    </div>
  );
};

export default Gallery;
