import React, { useState } from 'react';
import './Gallery.css';

function Gallery() {

    const [photo,setPhoto] = useState([]);
    
    const [result,setResult] = useState([]);

    const fetchImages = () => {
        fetch('https://api.unsplash.com/photos/?client_id=iU1nsbeOMcgen6Xb1x-T4zIGER6YnvJ3zPUc5ovOXew')
        .then(res => res.json())
        .then(data => {
            console.log(data)
            setResult(data.result)
        })
    }


  return (
    <div className='gallery'>
        <h1>Gallery</h1>
        <p>Here you can search whole lot of images from world wide stack.</p>
        <input onChange={(e) => setPhoto(e.target.photo)}  type='text' name='photos' placeholder='Search for Images here...' />
        <br/>
        <br/>
        <button onClick={() => fetchImages} type='submit'>Search</button>
        
       {results.map((item) => {
           return <img src= {item.urls.small} />
       })
    }
       
    </div>
  );
};

export default Gallery;
